","keccak256":"0x211639753e28bdca7f98618f51dca3dcd08a88b57c45050eb05fa4d0053327c3","license":"MIT"},"contracts/Swapper.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

import '@lbertenasco/contract-utils/contracts/utils/Governable.sol';
import '@lbertenasco/contract-utils/contracts/utils/CollectableDust.sol';

interface ISwapper {
  event Swapped(address _receiver, address _tokenIn, address _tokenOut, uint256 _amountIn, uint256 _maxSlippage, uint256 _receivedAmount);

  function SLIPPAGE_PRECISION() external view returns (uint256);

  function TRADE_FACTORY() external view returns (address);

  function swap(
    address _receiver,
    address _tokenIn,
    address _tokenOut,
    uint256 _amountIn,
    uint256 _maxSlippage
  ) external returns (uint256 _receivedAmount);
}

abstract contract Swapper is ISwapper, Governable, CollectableDust {
  using SafeERC20 for IERC20;

  uint256 public immutable override SLIPPAGE_PRECISION = 10000; // 1 is 0.0001%, 1_000 is 0.1%
  address public immutable override TRADE_FACTORY;

  constructor(address _governor, address _tradeFactory) Governable(_governor) {
    require(_tradeFactory != address(0), 'Swapper: zero address');
    TRADE_FACTORY = _tradeFactory;
  }

  modifier onlyTradeFactory() {
    require(msg.sender == TRADE_FACTORY, 'Swapper: not trade factory');
    _;
  }

  function _assertPreSwap(
    address _receiver,
    address _tokenIn,
    address _tokenOut,
    uint256 _amountIn,
    uint256 _maxSlippage
  ) internal pure {
    require(_receiver != address(0), 'Swapper: zero address');
    require(_tokenIn != address(0) && _tokenOut != address(0), 'Swapper: zero address');
    require(_amountIn > 0, 'Swapper: zero amount');
    require(_maxSlippage > 0, 'Swapper: zero slippage');
  }

  function _executeSwap(
    address _receiver,
    address _tokenIn,
    address _tokenOut,
    uint256 _amountIn,
    uint256 _maxSlippage
  ) internal virtual returns (uint256 _receivedAmount);

  function swap(
    address _receiver,
    address _tokenIn,
    address _tokenOut,
    uint256 _amountIn,
    uint256 _maxSlippage
  ) external virtual override onlyTradeFactory returns (uint256 _receivedAmount) {
    _assertPreSwap(_receiver, _tokenIn, _tokenOut, _amountIn, _maxSlippage);
    IERC20(_tokenIn).safeTransferFrom(TRADE_FACTORY, address(this), _amountIn);
    _receivedAmount = _executeSwap(_receiver, _tokenIn, _tokenOut, _amountIn, _maxSlippage);
    emit Swapped(_receiver, _tokenIn, _tokenOut, _amountIn, _maxSlippage, _receivedAmount);
  }

  function sendDust(
    address _to,
    address _token,
    uint256 _amount
  ) external virtual override onlyGovernor {
    _sendDust(_to, _token, _amount);
  }
}
","keccak256":"0xa211c784713162480d1e98f87f954701e90c95577dea0c144478605462e50b5e","license":"MIT"},"contracts/SwapperRegistry.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.4;

import '@openzeppelin/contracts/utils/structs/EnumerableSet.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';

import '@lbertenasco/contract-utils/contracts/utils/CollectableDust.sol';
import '@lbertenasco/contract-utils/contracts/utils/Governable.sol';

interface ISwapperRegistry {
  event SwapperAdded(address indexed _swapper, string _name);

  event SwapperDeprecated(address indexed _swapper);

  function nameByAddress(address) external view returns (string memory);

  function swapperByName(string memory) external view returns (address);

  function initializationByAddress(address) external view returns (uint256);

  function deprecatedByAddress(address) external view returns (bool);

  function swappers() external view returns (address[] memory _swappersAddresses);

  function swapperNames() external view returns (string[] memory _swappersNames);

  function activeSwappers() external view returns (address[] memory _activeSwappers);

  function deprecatedSwappers() external view returns (address[] memory _deprecatedSwappers);

  function isSwapper(address _swapper) external view returns (bool);

  function isSwapper(string memory _swapper)
    external
    view
    returns (
      bool _isSwapper,
      address _swapperAddress,
      uint256 _initialization
    );

  function addSwapper(string memory _name, address _swapper) external;

  function deprecateSwapper(address _swapper) external;
}

contract SwapperRegistry is ISwapperRegistry, CollectableDust, Governable {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.AddressSet;

  mapping(address => string) public override nameByAddress;
  mapping(string => address) public override swapperByName;
  mapping(address => uint256) public override initializationByAddress;
  mapping(address => bool) public override deprecatedByAddress;
  EnumerableSet.AddressSet internal _swappers;

  constructor(address _governance) Governable(_governance) {}

  function swappers() external view override returns (address[] memory _swappersAddresses) {
    _swappersAddresses = new address[](_swappers.length());
    for (uint256 i = 0; i < _swappers.length(); i++) {
      _swappersAddresses[i] = _swappers.at(i);
    }
  }

  function swapperNames() external view override returns (string[] memory _swappersNames) {
    _swappersNames = new string[](_swappers.length());
    for (uint256 i = 0; i < _swappers.length(); i++) {
      _swappersNames[i] = nameByAddress[_swappers.at(i)];
    }
  }

  function activeSwappers() external view override returns (address[] memory _activeSwappers) {
    _activeSwappers = new address[](_swappers.length());
    uint256 _totalActive = 0;
    for (uint256 i = 0; i < _swappers.length(); i++) {
      if (!deprecatedByAddress[_swappers.at(i)]) {
        _activeSwappers[_totalActive] = _swappers.at(i);
        _totalActive += 1;
      }
    }
  }

  function deprecatedSwappers() external view override returns (address[] memory _deprecatedSwappers) {
    _deprecatedSwappers = new address[](_swappers.length());
    uint256 _totalDeprecated = 0;
    for (uint256 i = 0; i < _swappers.length(); i++) {
      if (deprecatedByAddress[_swappers.at(i)]) {
        _deprecatedSwappers[_totalDeprecated] = _swappers.at(i);
        _totalDeprecated += 1;
      }
    }
  }

  function isSwapper(address _swapper) external view override returns (bool) {
    return _swappers.contains(_swapper);
  }

  function isSwapper(string memory _swapper)
    external
    view
    override
    returns (
      bool _isSwapper,
      address _swapperAddress,
      uint256 _initialization
    )
  {
    _swapperAddress = swapperByName[_swapper];
    _initialization = initializationByAddress[_swapperAddress];
    _isSwapper = _swapperAddress != address(0);
  }

  function addSwapper(string memory _name, address _swapper) external override onlyGovernor {
    _addSwapper(_name, _swapper);
  }

  function _addSwapper(string memory _name, address _swapper) internal {
    require(bytes(_name).length > 0, 'SwapperRegistry: empty name');
    require(_swapper != address(0), 'SwapperRegistry: zero address');
    require(!_swappers.contains(_swapper), 'SwapperRegistry: swapper already added');
    require(swapperByName[_name] == address(0), 'SwapperRegistry: name taken');
    nameByAddress[_swapper] = _name;
    swapperByName[_name] = _swapper;
    initializationByAddress[_swapper] = block.timestamp;
    _swappers.add(_swapper);
    emit SwapperAdded(_swapper, _name);
  }

  function deprecateSwapper(address _swapper) external override onlyGovernor {
    _deprecateSwapper(_swapper);
  }

  function _deprecateSwapper(address _swapper) internal {
    require(_swappers.contains(_swapper), 'SwapperRegistry: swapper not added');
    deprecatedByAddress[_swapper] = true;
    emit SwapperDeprecated(_swapper);
  }

  function sendDust(
    address _to,
    address _token,
    uint256 _amount
  ) external virtual override onlyGovernor {
    _sendDust(_to, _token, _amount);
  }
}
